import React from "react";
import { Navigate } from "react-router-dom"; // Use Navigate for redirection in React Router v6

const PrivateRoute = ({ element: Element, ...rest }) => {
  const token = localStorage.getItem("token"); // Check if token exists in localStorage

  return token ? (
    <Element {...rest} /> // If token exists, render the component
  ) : (
    <Navigate to="/" /> // Redirect to login page if no token
  );
};

export default PrivateRoute; // Make sure you are exporting it as default
