import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/AddPaintingPage.css";
import Navbar from "./Navbar"; // Adjust the path if needed
import axios from "axios"; // Import axios for making HTTP requests

const AddPaintingPage = () => {
  const [paintingImage, setPaintingImage] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [error, setError] = useState("");

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith("image/")) {
        alert("Please upload a valid image file!");
        return;
      }
      const reader = new FileReader();
      reader.onload = () => {
        setPaintingImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAddPainting = async () => {
    if (!title || !description || !paintingImage) {
      alert("Please fill in all fields and upload an image.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("painting", title, description);

      formData.append("image", paintingImage); // This is a base64 image
      const username = localStorage.username;
      const token = localStorage.getItem("token"); // Get JWT token from localStorage
      // Assuming your backend endpoint for adding paintings is '/api/paintings'
      const response = await axios.post(
        `http://localhost:8080/api/paintings/add-painting/${username}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data", // Important for file uploads
            Authorization: `Bearer ${token}`,
          },
        }
      );

      // Assuming the response contains the success message
      alert("Painting added successfully!");
      setPaintingImage(null);
      setTitle("");
      setDescription("");
    } catch (error) {
      console.error("Error uploading painting:", error);
      setError("Failed to add painting. Please try again.");
    }
  };

  return (
    <div className="add-painting-page">
      <Navbar />
      <div className="container-fluid p-4">
        <div className="row">
          {/* Left side for Image Upload */}
          <div className="col-md-6 d-flex flex-column align-items-center justify-content-center">
            <div className="image-upload-box">
              {paintingImage ? (
                <img
                  src={paintingImage}
                  alt="Uploaded Painting Preview"
                  className="uploaded-image"
                />
              ) : (
                <label htmlFor="file-upload" className="upload-placeholder">
                  +
                </label>
              )}
              <input
                id="file-upload"
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                style={{ display: "none" }}
              />
            </div>
          </div>

          {/* Right side for Title and Description */}
          <div className="col-md-6 d-flex flex-column">
            {error && <p className="text-danger">{error}</p>}
            <div className="mb-3">
              <label htmlFor="title" className="form-label">
                Title
              </label>
              <input
                type="text"
                id="title"
                className="form-control"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">
                Description
              </label>
              <textarea
                id="description"
                className="form-control"
                rows="5"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              ></textarea>
            </div>
            <button
              className="btn btn-primary align-self-end"
              onClick={handleAddPainting}
            >
              Add
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddPaintingPage;
