import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./Navbar";

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    // Fetch users when the component mounts
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:8080/api/users", {
          headers: {
            Authorization: `Bearer ${token}`, // Include JWT token
          },
        });
        setUsers(response.data);
      } catch (err) {
        setError("Failed to fetch users.");
      }
    };

    fetchUsers();
  }, []);

  const handleMakeAdmin = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:8080/api/users/${userId}/make-admin`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`, // Include JWT token
          },
        }
      );
      // Refresh user list after making a user admin
      const updatedUsers = users.map((user) =>
        user.id === userId ? { ...user, role: "admin" } : user
      );
      setUsers(updatedUsers);
    } catch (error) {
      setError("Failed to make user admin.");
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:8080/api/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`, // Include JWT token
        },
      });
      // Remove deleted user from the list
      setUsers(users.filter((user) => user.id !== userId));
    } catch (error) {
      setError("Failed to delete user.");
    }
  };

  return (
    <div className="container">
      <Navbar />
      <h1>Users</h1>
      {error && <p className="text-danger">{error}</p>}
      <ul className="list-group">
        {users.map((user) => (
          <li
            key={user.id}
            className="list-group-item d-flex justify-content-between align-items-center"
          >
            <div>
              <strong>{user.username}</strong> - <span>{user.role}</span>
            </div>
            <div>
              <button
                className="btn btn-primary btn-sm me-2"
                onClick={() => handleMakeAdmin(user.id)}
                disabled={user.role === "admin"} // Disable if already admin
              >
                Make Admin
              </button>
              <button
                className="btn btn-danger btn-sm"
                onClick={() => handleDeleteUser(user.id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UsersPage;
