import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/GalleryPage.css";
import Navbar from "./Navbar"; // Adjust the path if needed
import axios from "axios"; // Import axios for HTTP requests
import { jwtDecode } from "jwt-decode";

const GalleryPage = () => {
  const [selectedPainting, setSelectedPainting] = useState(null);
  const [paintings, setPaintings] = useState([]);
  const [userRatings, setUserRatings] = useState({});
  const [comments, setComments] = useState([]);
  const [error, setError] = useState(null);
  const [paintingRatings, setPaintingRatings] = useState([]);
  const [curentUser, setCurentUser] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [editingComment, setEditingComment] = useState(null); // Track comment being edited
  const [editedContent, setEditedContent] = useState(""); // Store the updated content

  const getCurrentUser = () => {
    const token = localStorage.getItem("token");
    if (token) {
      const decodedToken = jwtDecode(token);
      setCurentUser(decodedToken); // Returns the decoded user data
      console.log(decodedToken.sub);
      localStorage.setItem("username", decodedToken.sub);
    }
    return null; // No token, user not authenticated
  };

  useEffect(() => {
    getCurrentUser();
    const fetchPaintings = async () => {
      const token = localStorage.getItem("token"); // Get JWT token from localStorage

      if (!token) {
        setError("You must be logged in to view the paintings.");
        return;
      }

      try {
        console.log("JWT Token:", localStorage.getItem("token"));
        const response = await axios.get(
          "http://localhost:8080/api/paintings",
          {
            headers: {
              Authorization: `Bearer ${token}`, // Send the token in the Authorization header
            },
          }
        );
        setPaintings(response.data); // Set the fetched paintings
      } catch (error) {
        // Handle errors more gracefully
        if (error.response) {
          setError(
            `Error: ${error.response.data || "Could not fetch paintings"}`
          );
        } else if (error.request) {
          setError("Network error. Please check your connection.");
        } else {
          setError(`Unexpected error: ${error.message}`);
        }
        console.error(error);
      }
    };

    fetchPaintings();
  }, []);

  const handleSelectPainting = async (painting) => {
    setSelectedPainting(painting);
    setComments([]); // Clear previous comments
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(
        `http://localhost:8080/api/comments/painting/${painting.id}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setComments(response.data); // Set the fetched comments

      const ratingsResponse = await axios.get(
        `http://localhost:8080/api/ratings/painting/${painting.id}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setPaintingRatings(ratingsResponse.data); // Update state with fetched ratings
    } catch (error) {
      console.error("Error fetching comments:", error);
      setError("Failed to fetch comments.");
    }
  };

  const handleCommentSubmit = async () => {
    if (!newComment.trim()) return; // Do nothing if the comment is empty

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `http://localhost:8080/api/comments/painting/${selectedPainting.id}/${curentUser.sub}`,
        {
          content: newComment,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`, // Include JWT token
          },
        }
      );
      setComments([...comments, response.data]);
      setNewComment(""); // Clear the input field after submission
    } catch (error) {
      console.error("Error submitting comment:", error);
      setError("Failed to submit comment.");
    }
  };

  const handleEditComment = (comment) => {
    setEditingComment(comment.id);
    setEditedContent(comment.content);
  };

  const handleUpdateComment = async (commentId) => {
    if (!editedContent.trim()) return;

    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `http://localhost:8080/api/comments/${commentId}`,
        { content: editedContent },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setComments((prevComments) =>
        prevComments.map((comment) =>
          comment.id === commentId
            ? { ...comment, content: editedContent }
            : comment
        )
      );
      setEditingComment(null);
      setEditedContent("");
    } catch (error) {
      console.error("Error updating comment:", error);
      setError("Failed to update comment.");
    }
  };

  const handleStarClick = (rating) => {
    if (selectedPainting) {
      const newRatings = {
        ...userRatings,
        [selectedPainting.id]: rating,
      };
      setUserRatings(newRatings);
      const token = localStorage.getItem("token");
      axios
        .post(
          `http://localhost:8080/api/ratings/${selectedPainting.id}/${curentUser.sub}`,
          { score: rating },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        )
        .then((response) => {
          console.log("Rating updated:", response.data);
          handleSelectPainting(selectedPainting);
        })
        .catch((error) => {
          console.error("Error updating rating:", error);
          setError("Failed to update rating");
        });
    }
  };

  const calculateAverageRating = (ratings) => {
    if (!ratings || ratings.length === 0) return 0;
    const totalRating = ratings.reduce((sum, rating) => sum + rating.score, 0);
    return Math.round(totalRating / ratings.length);
  };

  const getUserRating = (ratings) => {
    if (!ratings || ratings.length === 0) return 0;

    const userRating = ratings.find((rating) => rating.user === curentUser.sub);
    return userRating ? parseInt(userRating.score) : 0;
  };

  const currentRating = selectedPainting
    ? calculateAverageRating(paintingRatings)
    : 0;

  const userRating = selectedPainting ? getUserRating(paintingRatings) : 0;

  const [isEditingDescription, setIsEditingDescription] = useState(false); // Track if editing description
  const [editedDescription, setEditedDescription] = useState(""); // Store the updated description

  const handleEditDescription = () => {
    setIsEditingDescription(true);
    setEditedDescription(selectedPainting.description || ""); // Populate with existing description
  };

  const handleEditPainting = () => {
    if (!selectedPainting) return;

    // Example logic to edit a painting:
    const newTitle = prompt(
      "Enter new title for the painting:",
      selectedPainting.title
    );
    if (newTitle && newTitle.trim()) {
      setSelectedPainting((prev) => ({
        ...prev,
        title: newTitle.trim(),
      }));

      // Optional: Make an API call to update the painting on the backend
      /*
      fetch(`/api/paintings/${selectedPainting.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ title: newTitle.trim() }),
      })
        .then((response) => response.json())
        .then((updatedPainting) => {
          console.log("Painting updated:", updatedPainting);
        })
        .catch((error) => console.error("Error updating painting:", error));
      */
    }
  };

  // Function to handle painting delete
  const handleDeletePainting = () => {
    if (!selectedPainting) return;

    // Confirm deletion
    const confirmDelete = window.confirm(
      `Are you sure you want to delete "${selectedPainting.title}"?`
    );
    if (confirmDelete) {
      // Example logic to remove the painting from state
      setPaintings((prevPaintings) =>
        prevPaintings.filter((painting) => painting.id !== selectedPainting.id)
      );
      setSelectedPainting(null); // Clear selection

      // Optional: Make an API call to delete the painting on the backend
      /*
      fetch(`/api/paintings/${selectedPainting.id}`, {
        method: "DELETE",
      })
        .then((response) => {
          if (response.ok) {
            console.log("Painting deleted");
          } else {
            console.error("Failed to delete painting");
          }
        })
        .catch((error) => console.error("Error deleting painting:", error));
      */
    }
  };

  const handleSaveDescription = async () => {
    if (!editedDescription.trim()) return;

    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `http://localhost:8080/api/paintings/${selectedPainting.id}/description`,
        { description: editedDescription },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setSelectedPainting({
        ...selectedPainting,
        description: response.data.description, // Update description locally
      });
      setIsEditingDescription(false);
    } catch (error) {
      console.error("Error updating description:", error);
      setError("Failed to update description.");
    }
  };

  const handleDeleteDescription = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(
        `http://localhost:8080/api/paintings/${selectedPainting.id}/description`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setSelectedPainting({
        ...selectedPainting,
        description: "", // Clear the description locally
      });
    } catch (error) {
      console.error("Error deleting description:", error);
      setError("Failed to delete description.");
    }
  };

  return (
    <div className="gallery-page">
      <Navbar />
      {error && <p className="error">{error}</p>}
      <div className="content d-flex">
        <div className="painting-list">
          {paintings.map((painting) => (
            <div
              key={painting.id}
              className="painting-item"
              onClick={() => handleSelectPainting(painting)}
            >
              <div className="painting-box">
                <img
                  src={`data:image/jpeg;base64,${painting.image}`}
                  style={{
                    maxWidth: "100%",
                    maxHeight: "400px",
                    objectFit: "contain",
                  }}
                />
              </div>
              <div>
                <h5>{painting.title}</h5>
                <p>{painting.painter}</p>
                <div className="stars">
                  {"★".repeat(painting.stars)}
                  {"☆".repeat(5 - painting.stars)}
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="painting-details">
          {selectedPainting ? (
            <>
              {/* Painting Title and Buttons */}
              <div
                className="painting-header"
                style={{
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                }}
              >
                <h3>{selectedPainting.title}</h3>
                <div>
                  <button
                    className="btn btn-warning btn-sm"
                    onClick={handleEditPainting}
                    style={{ marginRight: "10px" }}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={handleDeletePainting}
                  >
                    Delete
                  </button>
                </div>
              </div>
              <p>by: {selectedPainting.painter}</p>
              {/* Description Section */}
              <h5>Description:</h5>
              <div
                className="description-container"
                style={{ display: "flex", alignItems: "center" }}
              >
                {isEditingDescription ? (
                  <>
                    <textarea
                      value={editedDescription}
                      onChange={(e) => setEditedDescription(e.target.value)}
                      rows="2"
                      style={{ flex: "1", marginRight: "10px" }}
                    />
                    <button
                      className="btn btn-primary btn-sm"
                      onClick={handleSaveDescription}
                      style={{ marginRight: "5px" }}
                    >
                      Save
                    </button>
                    <button
                      className="btn btn-secondary btn-sm"
                      onClick={() => setIsEditingDescription(false)}
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <>
                    <p style={{ margin: "0", flex: "1" }}>
                      {selectedPainting.description ||
                        "No description available."}
                    </p>
                    <button
                      className="btn btn-warning btn-sm"
                      onClick={handleEditDescription}
                      style={{ marginLeft: "10px" }}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={handleDeleteDescription}
                      style={{ marginLeft: "5px" }}
                    >
                      Delete
                    </button>
                  </>
                )}
              </div>
              {/* Painting Image */}
              {selectedPainting.image && (
                <div className="painting-display">
                  <img
                    src={`data:image/jpeg;base64,${selectedPainting.image}`}
                    style={{
                      maxWidth: "100%",
                      maxHeight: "400px",
                      objectFit: "contain",
                    }}
                  />
                </div>
              )}
              {/* Ratings Section */}
              Average rating:
              <div className="stars">
                {"★".repeat(currentRating)}
                {"☆".repeat(5 - currentRating)}
              </div>
              My rating:
              <div className="stars mt-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <span
                    key={star}
                    onClick={() => handleStarClick(star)}
                    style={{
                      cursor: "pointer",
                      color: star <= userRating ? "gold" : "gray",
                      fontSize: "1.5rem",
                    }}
                  >
                    ★
                  </span>
                ))}
              </div>
              {/* Comments Section */}
              <div className="mt-3">
                <textarea
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="Write a comment..."
                  rows="3"
                  style={{ width: "100%" }}
                />
                <button
                  className="btn btn-primary mt-2"
                  onClick={handleCommentSubmit}
                >
                  Submit Comment
                </button>
              </div>
              <h5 className="mt-3">Comments</h5>
              <div className="comments-section">
                {comments.length > 0 ? (
                  comments.map((comment) => (
                    <div key={comment.id} className="comment">
                      <p>
                        <strong>{comment.author}</strong>:{" "}
                        {editingComment === comment.id ? (
                          <>
                            <textarea
                              value={editedContent}
                              onChange={(e) => setEditedContent(e.target.value)}
                              rows="3"
                              style={{ width: "100%" }}
                            />
                            <button
                              className="btn btn-primary mt-2 btn-sm"
                              onClick={() => handleUpdateComment(comment.id)}
                            >
                              Update Comment
                            </button>
                          </>
                        ) : (
                          <>
                            {comment.content}
                            {comment.modified && (
                              <span className="text-muted ml-2">(edited)</span>
                            )}
                          </>
                        )}
                      </p>
                      {curentUser.sub === comment.author && (
                        <button
                          className="btn btn-warning btn-sm"
                          onClick={() => handleEditComment(comment)}
                        >
                          Edit
                        </button>
                      )}
                    </div>
                  ))
                ) : (
                  <p>No comments yet.</p>
                )}
              </div>
            </>
          ) : (
            <h3>Select a painting to see details</h3>
          )}
        </div>
        ;
      </div>
    </div>
  );
};

export default GalleryPage;
