import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Navbar from "./Navbar"; // Import the Navbar component

const UpdateUserPage = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleUpdate = () => {
    if (password !== confirmPassword) {
      alert("Passwords don't match!");
      return;
    }

    console.log("Updated User Parameters:", {
      firstName,
      lastName,
      username,
      email,
      password,
    });

    alert("User parameters updated successfully!");
    // Reset the form (if needed)
    setPassword("");
    setConfirmPassword("");
  };

  const handleLogout = () => {
    alert("You have been logged out.");
    // Perform logout logic (e.g., clearing session or token)
    window.location.href = "/login"; // Redirect to the login page
  };

  const handleDeleteProfile = () => {
    if (
      window.confirm(
        "Are you sure you want to delete your profile? This action cannot be undone."
      )
    ) {
      console.log("User profile deleted.");
      alert("Your profile has been deleted.");
      // Perform profile deletion logic (e.g., API call)
      window.location.href = "/login"; // Redirect to the login page after deletion
    }
  };

  return (
    <div className="update-user-page">
      <Navbar />
      <div className="container mt-5">
        <h2 className="text-center mb-4">Update Your Information</h2>
        <div className="row">
          {/* Form */}
          <div className="col-md-8 offset-md-2">
            <div className="row mb-3">
              {/* First Name */}
              <div className="col-md-6">
                <label htmlFor="firstName" className="form-label">
                  First Name
                </label>
                <input
                  type="text"
                  id="firstName"
                  className="form-control"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                />
              </div>
              {/* Last Name */}
              <div className="col-md-6">
                <label htmlFor="lastName" className="form-label">
                  Last Name
                </label>
                <input
                  type="text"
                  id="lastName"
                  className="form-control"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                />
              </div>
            </div>

            {/* Username */}
            <div className="mb-3">
              <label htmlFor="username" className="form-label">
                Username
              </label>
              <input
                type="text"
                id="username"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>

            {/* Email */}
            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                Email Address
              </label>
              <input
                type="email"
                id="email"
                className="form-control"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            {/* Password */}
            <div className="mb-3">
              <label htmlFor="password" className="form-label">
                New Password
              </label>
              <input
                type="password"
                id="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            {/* Confirm Password */}
            <div className="mb-3">
              <label htmlFor="confirmPassword" className="form-label">
                Confirm Password
              </label>
              <input
                type="password"
                id="confirmPassword"
                className="form-control"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </div>

            {/* Action Buttons */}
            <div className="d-flex justify-content-between mt-4">
              <button
                className="btn btn-danger btn-lg"
                onClick={handleDeleteProfile}
              >
                Delete Profile
              </button>
              <button className="btn btn-primary btn-lg" onClick={handleUpdate}>
                Update Information
              </button>
              <button
                className="btn btn-secondary btn-lg"
                onClick={handleLogout}
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateUserPage;
