// Add/Edit/Delete Description Code in GalleryPage Component

// Inside your GalleryPage Component:

const [isEditingDescription, setIsEditingDescription] = useState(false); // Track if editing description
const [editedDescription, setEditedDescription] = useState(""); // Store the updated description

const handleEditDescription = () => {
  setIsEditingDescription(true);
  setEditedDescription(selectedPainting.description || ""); // Populate with existing description
};

const handleSaveDescription = async () => {
  if (!editedDescription.trim()) return;

  try {
    const token = localStorage.getItem("token");
    const response = await axios.put(
      `http://localhost:8080/api/paintings/${selectedPainting.id}/description`,
      { description: editedDescription },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    setSelectedPainting({
      ...selectedPainting,
      description: response.data.description, // Update description locally
    });
    setIsEditingDescription(false);
  } catch (error) {
    console.error("Error updating description:", error);
    setError("Failed to update description.");
  }
};

const handleDeleteDescription = async () => {
  try {
    const token = localStorage.getItem("token");
    await axios.delete(
      `http://localhost:8080/api/paintings/${selectedPainting.id}/description`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    setSelectedPainting({
      ...selectedPainting,
      description: "", // Clear the description locally
    });
  } catch (error) {
    console.error("Error deleting description:", error);
    setError("Failed to delete description.");
  }
};

// Inside the JSX rendering for painting-details:

<div className="painting-details">
  {selectedPainting ? (
    <>
      <h3>{selectedPainting.title}</h3>
      <p>by: {selectedPainting.painter}</p>
      {/* Description Section */}
      <h5>Description:</h5>
      {isEditingDescription ? (
        <>
          <textarea
            value={editedDescription}
            onChange={(e) => setEditedDescription(e.target.value)}
            rows="4"
            style={{ width: "100%" }}
          />
          <button
            className="btn btn-primary mt-2"
            onClick={handleSaveDescription}
          >
            Save
          </button>
          <button
            className="btn btn-secondary mt-2 ml-2"
            onClick={() => setIsEditingDescription(false)}
          >
            Cancel
          </button>
        </>
      ) : (
        <p>
          {selectedPainting.description || "No description available."}
          <button
            className="btn btn-warning btn-sm ml-2"
            onClick={handleEditDescription}
          >
            Edit
          </button>
          <button
            className="btn btn-danger btn-sm ml-2"
            onClick={handleDeleteDescription}
          >
            Delete
          </button>
        </p>
      )}
      {/* Existing Details */}
      {selectedPainting.image && (
        <div className="painting-display">
          <img
            src={`data:image/jpeg;base64,${selectedPainting.image}`}
            style={{
              maxWidth: "100%",
              maxHeight: "400px",
              objectFit: "contain",
            }}
          />
        </div>
      )}
      Average rating:
      <div className="stars">
        {"★".repeat(currentRating)}
        {"☆".repeat(5 - currentRating)}
      </div>
      My rating:
      <div className="stars mt-2">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            onClick={() => handleStarClick(star)}
            style={{
              cursor: "pointer",
              color: star <= userRating ? "gold" : "gray",
              fontSize: "1.5rem",
            }}
          >
            ★
          </span>
        ))}
      </div>
      <div className="mt-3">
        <textarea
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
          rows="3"
          style={{ width: "100%" }}
        />
        <button className="btn btn-primary mt-2" onClick={handleCommentSubmit}>
          Submit Comment
        </button>
      </div>
      <h5 className="mt-3">Comments</h5>
      <div className="comments-section">
        {comments.length > 0 ? (
          comments.map((comment) => (
            <div key={comment.id} className="comment">
              <p>
                <strong>{comment.author}</strong>:{" "}
                {editingComment === comment.id ? (
                  <>
                    <textarea
                      value={editedContent}
                      onChange={(e) => setEditedContent(e.target.value)}
                      rows="3"
                      style={{ width: "100%" }}
                    />
                    <button
                      className="btn btn-primary mt-2 btn-sm"
                      onClick={() => handleUpdateComment(comment.id)}
                    >
                      Update Comment
                    </button>
                  </>
                ) : (
                  <>
                    {comment.content}
                    {comment.modified && (
                      <span className="text-muted ml-2">(edited)</span>
                    )}
                  </>
                )}
              </p>
              {curentUser.sub === comment.author && (
                <button
                  className="btn btn-warning btn-sm"
                  onClick={() => handleEditComment(comment)}
                >
                  Edit
                </button>
              )}
            </div>
          ))
        ) : (
          <p>No comments yet.</p>
        )}
      </div>
    </>
  ) : (
    <h3>Select a painting to see details</h3>
  )}
</div>;
