import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/AddPaintingPage.css";
import Navbar from "./Navbar"; // Adjust the path if needed
import axios from "axios"; // Import axios for making HTTP requests
import { useNavigate, useParams } from "react-router-dom";

const EditPaintingPage = () => {
  const { paintingId } = useParams(); // Get the paintingId from the URL
  const [paintingImage, setPaintingImage] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  // Fetch painting details when the component loads
  useEffect(() => {
    const fetchPainting = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/paintings/${paintingId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        const { title, description, image } = response.data;
        setTitle(title);
        setDescription(description);
        setPaintingImage(image);
      } catch (err) {
        setError("Failed to load painting details.");
      }
    };

    fetchPainting();
  }, [paintingId]);

  // Handle image upload and preview
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setPaintingImage(reader.result); // Preview the uploaded image
      };
      reader.readAsDataURL(file);
    }
  };

  // Handle form submission
  const handleSave = async () => {
    if (!title || !description) {
      setError("Title and description are required.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("description", description);
      if (paintingImage instanceof File) {
        formData.append("image", paintingImage); // Include image if updated
      }

      await axios.put(`/api/paintings/${paintingId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      navigate("/gallery"); // Navigate back to the gallery page
    } catch (err) {
      setError("Failed to save painting. Please try again.");
    }
  };

  return (
    <div className="add-painting-page">
      <Navbar />
      <div className="container-fluid p-4">
        <div className="row">
          {/* Left side for Image Upload */}
          <div className="col-md-6 d-flex flex-column align-items-center justify-content-center">
            <div className="image-upload-box">
              <img src={`data:image/jpeg;base64,${paintingImage}`} />
            </div>
          </div>

          {/* Right side for Title and Description */}
          <div className="col-md-6 d-flex flex-column">
            {error && <p className="text-danger">{error}</p>}
            <div className="mb-3">
              <label htmlFor="title" className="form-label">
                Title
              </label>
              <input
                type="text"
                id="title"
                className="form-control"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">
                Description
              </label>
              <textarea
                id="description"
                className="form-control"
                rows="5"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              ></textarea>
            </div>
            <button
              className="btn btn-primary align-self-end"
              onClick={handleSave}
            >
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditPaintingPage;
