import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios"; // Import Axios for API requests
import Navbar from "./Navbar"; // Import the Navbar component

const UpdateUserPage = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState(""); // For displaying any error
  const [loading, setLoading] = useState(true); // For loading state

  // Function to fetch the current user data
  const fetchUserData = async () => {
    const token = localStorage.getItem("token"); // Retrieve token from localStorage

    if (!token) {
      alert("No token found. Please log in.");
      window.location.href = "/login"; // Redirect if token is missing
      return;
    }

    try {
      const response = await axios.get(
        `http://localhost:8080/users/${localStorage.getItem("username")}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const { firstName, lastName, username, email } = response.data;
      // Set the fields with current user data
      setFirstName(firstName);
      setLastName(lastName);
      setUsername(username);
      setEmail(email);
      setLoading(false); // Data is fetched, set loading to false
    } catch (error) {
      setLoading(false);
      console.error("Error fetching user data:", error);
      setError("Failed to load user data. Please try again.");
    }
  };

  // Call fetchUserData when component mounts
  useEffect(() => {
    fetchUserData();
  }, []);

  // Handle update button click
  const handleUpdate = async () => {
    if (password !== confirmPassword) {
      alert("Passwords don't match!");
      return;
    }

    // Prepare the updated user data
    const updatedUserData = {
      firstName,
      lastName,
      username,
      email,
      password,
    };

    const token = localStorage.getItem("token"); // Get token from localStorage

    try {
      // Make API call to update user data
      console.log(updatedUserData.usernam);
      const response = await axios.put(
        `http://localhost:8080/users/${localStorage.getItem("username")}`, // Update endpoint
        updatedUserData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      console.log("User updated:", response.data);
      alert("User parameters updated successfully!");
      // Optionally reset the form here
      setPassword("");
      setConfirmPassword("");
      if (updatedUserData.username != localStorage.getItem("username")) {
        handleLogout();
      }
    } catch (error) {
      console.error("Error updating user data:", error);
      setError("Failed to update user data. Please try again.");
    }
  };

  // Handle logout button click
  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token from localStorage
    alert("You have been logged out.");
    window.location.href = "/"; // Redirect to login page
  };

  // Handle delete profile button click
  const handleDeleteProfile = async () => {
    if (
      window.confirm(
        "Are you sure you want to delete your profile? This action cannot be undone."
      )
    ) {
      const token = localStorage.getItem("token");

      try {
        // Make API call to delete the profile
        await axios.delete("http://localhost:8080/api/user/delete", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        console.log("User profile deleted.");
        alert("Your profile has been deleted.");
        localStorage.removeItem("token"); // Remove token after profile deletion
        window.location.href = "/"; // Redirect to login page
      } catch (error) {
        console.error("Error deleting profile:", error);
        alert("Failed to delete profile. Please try again.");
      }
    }
  };

  // Render loading state or the actual form
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="update-user-page">
      <Navbar />
      <div className="container mt-5">
        <h2 className="text-center mb-4">Update Your Information</h2>
        <div className="row">
          {/* Form */}
          <div className="col-md-8 offset-md-2">
            <div className="row mb-3">
              {/* First Name */}
              <div className="col-md-6">
                <label htmlFor="firstName" className="form-label">
                  First Name
                </label>
                <input
                  type="text"
                  id="firstName"
                  className="form-control"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                />
              </div>
              {/* Last Name */}
              <div className="col-md-6">
                <label htmlFor="lastName" className="form-label">
                  Last Name
                </label>
                <input
                  type="text"
                  id="lastName"
                  className="form-control"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                />
              </div>
            </div>

            {/* Username */}
            <div className="mb-3">
              <label htmlFor="username" className="form-label">
                Username
              </label>
              <input
                type="text"
                id="username"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>

            {/* Email */}
            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                Email Address
              </label>
              <input
                type="email"
                id="email"
                className="form-control"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            {/* Password */}
            <div className="mb-3">
              <label htmlFor="password" className="form-label">
                New Password
              </label>
              <input
                type="password"
                id="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            {/* Confirm Password */}
            <div className="mb-3">
              <label htmlFor="confirmPassword" className="form-label">
                Confirm Password
              </label>
              <input
                type="password"
                id="confirmPassword"
                className="form-control"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </div>

            {/* Action Buttons */}
            <div className="d-flex justify-content-between mt-4">
              <button
                className="btn btn-danger btn-xs"
                onClick={handleDeleteProfile}
              >
                Delete Profile
              </button>
              <button className="btn btn-primary btn-xs" onClick={handleUpdate}>
                Update Information
              </button>
              <button
                className="btn btn-secondary btn-xs"
                onClick={handleLogout}
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateUserPage;
