import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/GalleryPage.css";
import Navbar from "./Navbar"; // Adjust the path if needed
import axios from "axios"; // Import axios for HTTP requests

const GalleryPage = () => {
  const [selectedPainting, setSelectedPainting] = useState(null);
  const [paintings, setPaintings] = useState([]);
  const [userRatings, setUserRatings] = useState({});
  const [comments, setComments] = useState([]);

  // Fetch paintings from the backend when the component mounts
  useEffect(() => {
    axios
      .get("http://localhost:8080/api/paintings") // Assuming the backend endpoint is like this
      .then((response) => {
        setPaintings(response.data); // Set the fetched paintings
      })
      .catch((error) => {
        console.error("Error fetching paintings", error);
      });
  }, []);

  const handleSelectPainting = (painting) => {
    setSelectedPainting(painting);
    // Set up the comments for this painting (if needed)
    setComments([
      "Beautiful painting!",
      "Amazing detail.",
      "Could use some more colors.",
      "Fantastic work!",
    ]);
  };

  const handleStarClick = (rating) => {
    if (selectedPainting) {
      const newRatings = {
        ...userRatings,
        [selectedPainting.id]: rating, // Update the rating for the current painting
      };
      setUserRatings(newRatings);

      // Send the rating to the backend
      axios
        .post("http://localhost:8080/api/paintings/rate", {
          paintingId: selectedPainting.id,
          rating: rating,
        })
        .then((response) => {
          console.log("Rating updated:", response.data);
        })
        .catch((error) => {
          console.error("Error updating rating:", error);
        });
    }
  };

  const currentRating = selectedPainting
    ? userRatings[selectedPainting.id] || selectedPainting.stars
    : 0; // Default to the current rating or 0 if no rating exists

  return (
    <div className="gallery-page">
      <Navbar />

      <div className="content d-flex">
        {/* Left Scrollable Paintings Panel */}
        <div className="painting-list">
          {paintings.map((painting) => (
            <div
              key={painting.id}
              className="painting-item"
              onClick={() => handleSelectPainting(painting)}
            >
              <div className="painting-box"></div>
              <div>
                <h5>{painting.name}</h5>
                <p>{painting.artist}</p>
                <div className="stars">
                  {"★".repeat(painting.stars)}
                  {"☆".repeat(5 - painting.stars)}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Main Painting Details Section */}
        <div className="painting-details">
          {selectedPainting ? (
            <>
              <h3>{selectedPainting.name}</h3>
              <div className="painting-display"></div>
              <div className="stars">
                {"★".repeat(selectedPainting.stars)}
                {"☆".repeat(5 - selectedPainting.stars)}
              </div>
              {/* Interactive Stars */}
              <div className="stars mt-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <span
                    key={star}
                    onClick={() => handleStarClick(star)} // Set rating on click
                    style={{
                      cursor: "pointer",
                      color: star <= currentRating ? "gold" : "gray", // Highlight stars based on user rating
                      fontSize: "1.5rem",
                    }}
                  >
                    ★
                  </span>
                ))}
              </div>
              <h5 className="mt-3">Comments</h5>
              <div className="comments-section">
                {comments.map((comment, index) => (
                  <p key={index}>{comment}</p>
                ))}
              </div>
            </>
          ) : (
            <h3>Select a painting to see details</h3>
          )}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
