import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/GalleryPage.css";
import Navbar from "./Navbar"; // Adjust the path if needed
import axios from "axios"; // Import axios for HTTP requests
import { jwtDecode } from "jwt-decode";

const GalleryPage = () => {
  const [selectedPainting, setSelectedPainting] = useState(null);
  const [paintings, setPaintings] = useState([]);
  const [userRatings, setUserRatings] = useState({});
  const [comments, setComments] = useState([]);
  const [error, setError] = useState(null);
  const [paintingRatings, setPaintingRatings] = useState([]);
  const [curentUser, setCurentUser] = useState([]);

  // Fetch paintings from the backend when the component mounts
  useEffect(() => {
    const fetchPaintings = async () => {
      const token = localStorage.getItem("token"); // Get JWT token from localStorage

      if (!token) {
        setError("You must be logged in to view the paintings.");
        return;
      }

      try {
        console.log("JWT Token:", localStorage.getItem("token"));
        const response = await axios.get(
          "http://localhost:8080/api/paintings",
          {
            headers: {
              Authorization: `Bearer ${token}`, // Send the token in the Authorization header
            },
          }
        );
        setPaintings(response.data); // Set the fetched paintings
      } catch (error) {
        // Handle errors more gracefully
        if (error.response) {
          // Error with response from the server (e.g., 401 Unauthorized)
          setError(
            `Error: ${error.response.data || "Could not fetch paintings"}`
          );
        } else if (error.request) {
          // Error in making the request
          setError("Network error. Please check your connection.");
        } else {
          // Something else went wrong
          setError(`Unexpected error: ${error.message}`);
        }
        console.error(error);
      }
    };

    fetchPaintings();
  }, []);

  const getCurrentUser = () => {
    const token = localStorage.getItem("token");
    if (token) {
      const decodedToken = jwtDecode(token);
      setCurentUser(decodedToken); // Returns the decoded user data
    }
    return null; // No token, user not authenticated
  };

  getCurrentUser();

  const handleSelectPainting = async (painting) => {
    setSelectedPainting(painting);
    setComments([]); // Clear previous comments
    // Set up the comments for this painting (if needed)
    try {
      // Fetch comments for the selected painting
      const token = localStorage.getItem("token");
      const response = await axios.get(
        `http://localhost:8080/api/comments/painting/${painting.id}`,
        {
          headers: {
            Authorization: `Bearer ${token}`, // Include JWT token
          },
        }
      );
      setComments(response.data); // Set the fetched comments

      const ratingsResponse = await axios.get(
        `http://localhost:8080/api/ratings/painting/${painting.id}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setPaintingRatings(ratingsResponse.data); // Update state with fetched ratings
    } catch (error) {
      console.error("Error fetching comments:", error);
      setError("Failed to fetch comments.");
    }
  };

  const handleStarClick = (rating) => {
    if (selectedPainting) {
      const newRatings = {
        ...userRatings,
        [selectedPainting.id]: rating, // Update the rating for the current painting
      };
      setUserRatings(newRatings);

      // Send the rating to the backend with JWT token
      axios
        .post(
          "http://localhost:8080/api/paintings/rate",
          {
            paintingId: selectedPainting.id,
            rating: rating,
          },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`, // Include JWT token
            },
          }
        )
        .then((response) => {
          console.log("Rating updated:", response.data);
        })
        .catch((error) => {
          console.error("Error updating rating:", error);
          setError("Failed to update rating");
        });
    }
  };

  // Calculate the average rating of the painting
  const calculateAverageRating = (ratings) => {
    if (!ratings || ratings.length === 0) return 0;
    const totalRating = ratings.reduce((sum, rating) => sum + rating.score, 0);
    const average = totalRating / ratings.length;
    return Math.round(average); // Round to the nearest integer
  };

  const getUserRating = (ratings) => {
    if (!ratings || ratings.length === 0) return 0;
    ratings.forEach((rating) => {
      if (rating.user == curentUser.username) {
        return rating.score;
      }
    });
  };

  // Get the average rating for the selected painting
  const currentRating = selectedPainting
    ? calculateAverageRating(paintingRatings) // Calculate the average rating
    : 0; // Default to 0 if no selected painting

  const userRating = selectedPainting ? getUserRating(paintingRatings) : 0;
  return (
    <div className="gallery-page">
      <Navbar />
      {error && <p className="error">{error}</p>} {/* Display error if any */}
      <div className="content d-flex">
        {/* Left Scrollable Paintings Panel */}
        <div className="painting-list">
          {paintings.map((painting) => (
            <div
              key={painting.id}
              className="painting-item"
              onClick={() => handleSelectPainting(painting)}
            >
              <div className="painting-box">
                <img
                  src={`data:image/jpeg;base64,${painting.image}`}
                  style={{
                    maxWidth: "100%",
                    maxHeight: "400px",
                    objectFit: "contain",
                  }}
                />
              </div>
              <div>
                <h5>{painting.title}</h5>
                <p>{painting.painter}</p>
                <div className="stars">
                  {"★".repeat(painting.stars)}
                  {"☆".repeat(5 - painting.stars)}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Main Painting Details Section */}
        <div className="painting-details">
          {selectedPainting ? (
            <>
              <h3>{selectedPainting.title}</h3>
              <p>by: {selectedPainting.painter}</p>
              {/* Conditionally render the painting image */}
              {selectedPainting.image && (
                <div className="painting-display">
                  <img
                    src={`data:image/jpeg;base64,${selectedPainting.image}`}
                    style={{
                      maxWidth: "100%",
                      maxHeight: "400px",
                      objectFit: "contain",
                    }}
                  />
                </div>
              )}
              Avrage rating:
              <div className="stars">
                {"★".repeat(currentRating)}
                {"☆".repeat(5 - currentRating)}
              </div>
              {/* Interactive Stars */}
              My rating:
              <div className="stars mt-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <span
                    key={star}
                    onClick={() => handleStarClick(star)} // Set rating on click
                    style={{
                      cursor: "pointer",
                      color: star <= userRating ? "gold" : "gray", // Highlight stars based on user rating
                      fontSize: "1.5rem",
                    }}
                  >
                    ★
                  </span>
                ))}
              </div>
              <h5 className="mt-3">Comments</h5>
              <div className="comments-section">
                {comments.length > 0 ? (
                  comments.map((comment) => (
                    <p>
                      {comment.content} by: {comment.author}
                    </p>
                  ))
                ) : (
                  <p>No comments yet.</p>
                )}
              </div>
            </>
          ) : (
            <h3>Select a painting to see details</h3>
          )}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
